import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function Dashboard() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const load = async () => {
      setLoading(true); setError('');
      try {
        const { data } = await api.get('/user/dashboard');
        setData(data);
      } catch (e) {
        setError(e?.response?.data?.error || 'Failed to load dashboard');
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  if (loading) return <div className="container">Loading...</div>;
  if (error) return <div className="container">{error}</div>;
  if (!data) return null;

  const total = data.completedCount + (data.bookmarkedCount || 0) || 1;
  const pct = Math.round((data.completedCount / total) * 100);

  return (
    <div className="container">
      <div className="card" style={{ marginTop: 16 }}>
        <div className="row" style={{ justifyContent: 'space-between' }}>
          <div className="section-title">Your Progress</div>
          <div className="badge">{pct}%</div>
        </div>
        <div style={{ height: 10, background: 'var(--border)', borderRadius: 999, overflow: 'hidden', marginTop: 8 }}>
          <div style={{ width: `${pct}%`, height: '100%', background: 'var(--accent)' }} />
        </div>
      </div>

      <div className="card" style={{ marginTop: 16 }}>
        <div className="section-title">Bookmarked Questions</div>
        {data.bookmarkedQuestions?.length ? (
          <ul>
            {data.bookmarkedQuestions.map((b) => (
              <li key={b._id} style={{ padding: '8px 0', borderBottom: '1px dashed var(--border)' }}>
                <a href={b.question.url} target="_blank" rel="noreferrer">{b.question.title}</a>
                <span className={`tag ${b.question.difficulty.toLowerCase()}`} style={{ marginLeft: 8 }}>
                  <span className="dot" /> {b.question.difficulty}
                </span>
              </li>
            ))}
          </ul>
        ) : (
          <div className="muted">No bookmarks yet.</div>
        )}
      </div>
    </div>
  );
}
