import React, { useState } from 'react';

export default function Navbar({
  user,
  onLogout,
  onOpenAuth,
  onSearch,
  onDifficulty,
  onSort,
  theme,
  onToggleTheme,
  onVoiceToggle,
  recognizing,
}) {
  const [term, setTerm] = useState('');
  const [difficulty, setDifficulty] = useState('');
  const [sortBy, setSortBy] = useState('title_asc');

  const handleSearch = (e) => {
    const value = e.target.value;
    setTerm(value);
    onSearch(value);
  };
  const handleDiff = (e) => {
    setDifficulty(e.target.value);
    onDifficulty(e.target.value);
  };
  const handleSort = (e) => {
    setSortBy(e.target.value);
    onSort(e.target.value);
  };

  return (
    <div className="navbar">
      <div className="navbar-inner container">
        <div className="brand">DSA Menu</div>
        <input className="input" value={term} onChange={handleSearch} placeholder="Search questions..." />
        <select className="select" value={difficulty} onChange={handleDiff}>
          <option value="">All</option>
          <option value="Easy">Easy</option>
          <option value="Medium">Medium</option>
          <option value="Hard">Hard</option>
        </select>
        <select className="select" value={sortBy} onChange={handleSort}>
          <option value="title_asc">Title ‚Üë</option>
          <option value="title_desc">Title ‚Üì</option>
          <option value="difficulty_asc">Difficulty ‚Üë</option>
          <option value="difficulty_desc">Difficulty ‚Üì</option>
        </select>
        <div className="spacer" />
        <button className="icon-btn" onClick={onVoiceToggle} title="Voice commands">
          {recognizing ? 'üéôÔ∏è' : 'üé§'}
        </button>
        <button className="button ghost" onClick={onToggleTheme}>{theme === 'light' ? 'Dark' : 'Light'}</button>
        {user ? (
          <>
            <span className="badge">{user.name}</span>
            <button className="button" onClick={onLogout}>Logout</button>
          </>
        ) : (
          <button className="button" onClick={onOpenAuth}>Login / Register</button>
        )}
      </div>
    </div>
  );
}
